mapper.select:
select idx ,title ,count ,name ,id from bbs where useyn = 'Y' and category = #{category}
and title like concat('%', #{keyword}, '%')
and contents like concat('%', #{keyword}, '%')
and name like concat('%', #{keyword}, '%')
order by idx desc limit #{startList}, #{listSize}

mapper.update:
update bbs set 
	title = #{title}
   ,contents = #{contents}
   ,moddate = NOW()
where idx = #{idx}

update bbs set 
	count = count + 1
where idx = #{idx}
